mapper.select:
select m.user_id as "userId", m.group_id as "groupId", m.apply_reason as "applyReason", m.phone as "phone", m.status as "status",
u.user_id as "user.userId", u.headPicture as "user.headPicture", u.loginEmail as "user.loginEmail", u.username as "user.username", u.user_dec as "user.userDec", u.sex as "user.sex", u.age as "user.age", u.wechat as "user.weChat", u.phone as "user.phone" from member as m inner join `user` as u on m.user_id = u.user_id
m.group_id = #{groupId} and m.status = #{status} order by m.user_id limit ${offset},${limit};

mapper.update:
update member


		apply_reason = #{applyReason},


		phone = #{phone},

	status = #{status},
	role_id = #{roleId}

		user_id = #{userId} and group_id = #{groupId};

mapper.select:
select g.group_id as "groupId", g.group_icon as "groupIcon", g.group_name as "groupName", g.group_dec as "groupDec", g.group_contact as "groupContact", g.group_address as "groupAddress", g.group_type as "groupType", g.owner as "owner", g.visitors as "visitors", g.group_members as "groupMembers", g.group_status as "groupStatus", g.group_evidence as "groupEvidence",
u.user_id as "groupOwner.user_id", u.headPicture as "groupOwner.headPicture", u.loginEmail as "groupOwner.loginEmail", u.username as "groupOwner.username", u.user_dec as "groupOwner.userDec", u.sex as "groupOwner.sex", u.age as "groupOwner.age", u.wechat as "groupOwner.weChat", u.phone as "groupOwner.phone" from `group` as g inner join `user` as u on g.owner = u.user_id
g.group_id = #{groupId};

	select

	from `group`

		group_type = #{groupType}


        ORDER BY ${order} DESC


        limit #{offset},#{limited};
